import { useState } from "react";
import { Link } from "react-router-dom";
import Header from "./Header";

export default function Register(props) {
  const defaultValues = {
    email: "",
    password: "",
  };
  const [formValue, setFormValue] = useState({ email: '', password: '' });

  function handleChange(evt) {
    const { name, value } = evt.target;
    setFormValue({ ...formValue, [name]: value });
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    resetForm();
    props.onRegister(formValue);
  }
  function resetForm() {
    setFormValue({ ...defaultValues });
  }

  return (
    <>
      <Header headerText="Войти" link="/sign-in"/>
      <div className="authorization">
        <form className="authorization__form"
          onSubmit={handleSubmit}>
          <h3 className="authorization__heading">Регистрация</h3>
          <input
            className="authorization__input"
            type="email"
            name="email"
            placeholder="Email"
            minLength={2}
            maxLength={200}
            required
            onChange={handleChange}
            value={formValue.email}
          />
          <input
            className="authorization__input"
            type="password"
            name="password"
            placeholder="Пароль"
            minLength={2}
            maxLength={200}
            required
            value={formValue.password}
            onChange={handleChange}
          />
          <button className="authorization__submit-button"
            type="submit">Зарегистрироваться</button>
          <Link to="/sign-in"
            className="authorization__text">Уже зарегистрированы? Войти</Link>
        </form>
      </div>
    </>
  )
}
