import { Route, Switch, useHistory } from 'react-router-dom';
import Main from './Main.js'
import Footer from './Footer.js'
import PopupWithForm from './PopupWithForm.js'
import ImagePopup from './ImagePopup.js'
import { useEffect, useState } from 'react'
import CurrentUserContext from '../contexts/CurrentUserContext.js'
import api from '../utils/api.js'
import EditProfilePopup from './EditProfilePopup.js'
import EditAvatarPopup from './EditAvatarPopup.js'
import AddPlacePopup from './AddPlacePopup.js'
import Login from './Login.js';
import Register from './Register.js';
import InfoTooltip from './InfoTooltip.js'
import ProtectedRoute from './ProtectedRoute.js'
import { register, login, checkToken } from '../utils/auth.js';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false)
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false)
  const [isTooltipOpen, setIsTooltipOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null)
  const [currentUser, setCurrentUser] = useState({})
  const [cards, setCards] = useState([])

  //авторизация
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  //Стейт для авторизации. Состояние успешности автризации пользователя.
  const [isAuth, setIsAuth] = useState(false);
  //Стейт для данных залогиненного пользователя. Содержит _id и email для отображения в приложении
  const [email, setEmail] = useState('');

  const history = useHistory ()

  // Показ/скрытие модалки об успешной/ неудачной регистрации
  function openRegistrationStatus() {
    setIsTooltipOpen(true);
    window.addEventListener('click', handleCloseOnOverlay);
  }

  const handleRegister = (data) => {
    const { email, password } = data;
    return register(email, password)
      .then((res) => {
        if (res) {
          setIsAuth(true);
          openRegistrationStatus();
          history.push('/sign-in');
        }
      })
      .catch((err) => {
        setIsAuth(false);
        openRegistrationStatus();
        console.log(`Произошла ошибка: ${err}`);
        history.push('/sign-up');
      });
  };

  /**
   * Авторизация пользователя
   * @param data - введенные человеком логин и пароль
   */
  const handleLogin = (data) => {
    const { email, password } = data;
    setEmail(email);
    login(email, password)
      .then((res) => {
        if (res.token) {
          localStorage.setItem('jwt', res.token);
          setIsLoggedIn(true);
          setIsAuth(true);
          history.push('/');
        }
      })
      .catch((err) => {
        setIsAuth(false);
        openRegistrationStatus();
        console.log(`Произошла ошибка: ${err}`);
      });
  };

  /**
   * Обновление стейтов пользователя на основе токена.
   * Если токен есть, устанавливаем IsloggedIn и Email
   */
  useEffect(() => {
    if (localStorage.getItem('jwt')) {
      const jwt = localStorage.getItem('jwt');

      checkToken(jwt)
        .then((res) => {
          if(res) {
            setIsLoggedIn(true);
            setEmail(res.data.email);
          }
        })
        .catch((err) => {
          openRegistrationStatus();
          console.log(`Произошла ошибка: ${err}`);
        });
    }
  }, [openRegistrationStatus, history, isLoggedIn]);

  useEffect(() => {
    if (isLoggedIn) {
      history.push('/');
    }
  }, [history, isLoggedIn]);

  // Логаут пользователя из системы
  const handleLogout = () => {
    localStorage.removeItem('jwt');
    setIsLoggedIn(false);
    setIsAuth(false);
    history.push('/sign-in');
  };

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }
  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsTooltipOpen(false)
    setSelectedCard(null)
  }
  function handleCloseOnOverlay(evt) {
    if (evt.target.classList.contains('popup_opened')) {
      closeAllPopups();
    }
  }
  function handleCardClick(props) {
    setSelectedCard(props)
  }
  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((error) => {
        console.log(`Ошибка в блоке лайк, ${error}`)
      })
  }
  function handleCardDelete(card) {
    api.deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter(item => item._id !== card._id));
      })
      .catch((error) => {
        console.log(`Ошибка при удалении карточки, ${error}`)
      })
  }
  function handleUpdateUser(data) {
    api.setUserInfo(data)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((error) => {
        console.log(`Ошибка при внесении данных пользователя, ${error}`)
      })
  }
  function handleUpdateAvatar({ avatar }) {
    api.setUserAvatar(avatar)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((error) => {
        console.log(`Ошибка при изменении аватара, ${error}`)
      })
  }
  function handleAddPlaceSubmit(cardData) {
    api.addCard(cardData)
      .then((res) => {
        setCards([res, ...cards]);
        closeAllPopups();
      })
      .catch((error) => {
        console.log(`Ошибка при добавлении карточки, ${error}`)
      })
  }

  useEffect(() => {
    Promise.all([api.getUserInfo(), api.getCards()])
      .then(([dataUser, dataCards]) => {
        setCurrentUser(dataUser)
        setCards(dataCards)
      })
      .catch((error) => {
        console.log(`Ошибка, ${error}`)
      })
  }, [])

  return (
  <CurrentUserContext.Provider value={currentUser}>
    <div className="App">
      <div className="page">
        <Switch>
          <ProtectedRoute
            exact path="/"
            isLoggedIn={isLoggedIn}
            component={Main}
            onEditProfile={handleEditProfileClick}
            onEditAvatar={handleEditAvatarClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
            onCardLike={handleCardLike}
            onCardDelete={handleCardDelete}
            cards={cards}
            logout={handleLogout}
            email={email}
          />

          <Route path="/sign-up">
            <Register handleRegister={handleRegister} />
          </Route>

          <Route path="/sign-in">
          <Login handleLogin={handleLogin} />
          </Route>
        </Switch>

        <Footer/>

        <EditProfilePopup
        onUpdateUser={handleUpdateUser}
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        />

        <AddPlacePopup
        onAddPlace={handleAddPlaceSubmit}
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
        />

        <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateAvatar}/>

        <PopupWithForm
        name="#delete-popup"
        title="Вы уверены?"
        buttonText="Да"
        />
        <ImagePopup
        card={selectedCard}
        onClose={closeAllPopups}
        />
        <InfoTooltip
          isOpen={isTooltipOpen}
          onClose={closeAllPopups}
          isRegSuccess={isAuth}
          regSuccess="Вы успешно зарегестрировались!"
          regFailed="Что-то пошло не так! Попробуйте еще раз."
        />
      </div>
    </div>
  </CurrentUserContext.Provider>
  );
}

export default App;
